DelayProgram:				; XREF: PauseGame
		move.b	d0,$FFFFF62A	; save the VInt routine counter
		move	#$2300,sr	; enable interrupts

.WaitVInt	tst.b	$FFFFF62A	; is VInt routine 0?
		bne	.WaitVInt	; if not, branch
		rts
; End of function DelayProgram

; ---------------------------------------------------------------------------
