; ---------------------------------------------------------------------------

LevelSelect:
		moveq	#4,d0
		bsr	DelayProgram
		bsr	LevSelControls
		bsr	RunPLC_RAM
	;	tst.l	($FFFFF680).w
	;	bne.s	LevelSelect
		andi.b	#$F0,$FFFFF605	; is A, B, C, or Start pressed?
		beq.s	LevelSelect	; if not, branch
		move.w	$FFFFFF82,d0
		cmpi.w	#$14,d0		; have you selected item $14 (sound test)?
		bne.s	LevSel_Level_SS	; if not, go to	Level/SS subroutine

		move.b	$FFFFF605,d0	; move the current button press to d0
		and.b	#$40,d0		; is A pressed?
		beq.s	.CheckB		; if not, branch

		addi.w	#$10,$FFFFFF84	; add 16 to the music ID
		cmpi.w	#$80,$FFFFFF84	; is the ID over $80?
		blt	LevelSelect2	; if not, branch

		subi.w	#$80,$FFFFFF84	; subtract $80 from musicID
		bra	LevelSelect2	; display the numbers

.CheckB		move.b	$FFFFF605,d0	; move the current button press to d0
		and.b	#$10,d0		; is B pressed?
		beq	.Continue	; if not, branch
		subi.w	#$10,$FFFFFF84	; subtract 16 from the music ID
		bge	LevelSelect2	; if greater than 0, branch
		addi.w	#$80,$FFFFFF84	; add $80 to the ID
		bra	LevelSelect2	; display it

.Continue	move.w	($FFFFFF84).w,d0
		addi.w	#$80,d0
	;	cmpi.w	#$FF,d0		; is sound $E5-$FF being played?
	;	bgt.s	LevelSelect	; if yes, branch

LevSel_PlaySnd:
		bsr.w	PlaySound
		bra	LevelSelect
