LevSel_Level_SS:			; XREF: LevelSelect
		add.w	d0,d0
		add.w	d0,d0
		move.l	LSelectPointers(pc,d0.w),a1; get the address id

		cmpa.w	#SRAM_ZoneComplete,a1; is this flag in a1?
		beq	.ChkZones	; if is, branch away
		cmpa.w	#SRAM_Turbo,a1
		beq	.SetTurbo

		bchg	#0,(a1)		; switch bit 0 of a1
		cmpa.w	#$FE02,a1	; is level set to restart
		bne	LevelSelect	; if not, branch
		move.w	#1,(a1)		; set to restart
		addq.l	#4,sp		; return from the level select
		rts			; return from PauseGame routine

.ChkZones	moveq	#0,d1		; clear d1
		cmp.w	(a1),d1		; is any zones complete?
		bne	.0		; if is, branch
		subq.w	#1,d1 		; set all to complete
.0		move.w	d1,(a1)		; set value to a1
		bra	LevelSelect
		
.SetTurbo	move.l	SRAM_SlotID,a1	; get slot ID
		cmpi.b	#$85,SRAM_Turbo(a1); check turbo mode
		bne	.		; if not set, branch
		move.b	#0,SRAM_Turbo(a1); clear turbo
		bra	LevelSelect

.		move.b	#$85,SRAM_Turbo(a1); set turbo
		bra	LevelSelect
