; ---------------------------------------------------------------------------
; Special routine to load save file frames with correct information
; ---------------------------------------------------------------------------

LoadSaveFileFrames:
		moveq	#0,d2
		moveq	#4-1,d6			; repeat 3 times
		lea	SRAM_Slot1+SRAM_Progress,a2; get progress counter to a2

.loop		move.l	SSF_VDPlist(pc,d2),d5	; get proper VDP setting
		move.w	#$24B6,d3		; VRAM address
		cmp.b	Spindash_HorizDelay,d2	; is this save highlighted?
		bne	.NoHighlight
                move.w	#$44B6,d3		; highlighted tiles

.NoHighlight	lea	SFF_FileEmpty,a1	; get proper frame
		tst.b	(a2)			; is any progress been made (aka file started)?
	;	beq	.LoadASCII		; if not, branch
		bmi	.LoadASCII		; if negative, branch
		lea	SFF_InUse,a1		; get in use frame

.LoadASCII	move.l	d5,-(sp)		; store the VDP setting to stack
		bsr	LoadASCII		; load the frame to plane
                move.l	(sp)+,d5		; pop it right back

		moveq	#0,d0
		move.b	d2,d0			; copy low byte of d2 to d0

		lsl.w	#4,d0			; shift left 4 times (multiply by 16 (For object slot size))
		lea	Object_RAM+$40,a1	; get object RAM to a1
		add.w	d0,a1			; add d0 to a1

		move.b	#0,(a1)			; clear the object ID
		move.w	#0,$100(a1)		; clear Tails tail
		tst.b	(a2)			; is any progress been made (aka file started)?
	;	beq	.NoSpecial		; if not, branch
		bmi	.NoSpecial		; if negative, branch

		move.b	#$92,(a1)		; set to static player object
		clr.b	Routine(a1)		; clear routine counter
		move.l	a2,Off3C(a1)		; save SRAM location
		subi.w	#SRAM_Progress-SRAM_LastLVL,Off3E(a1); save proper SRAM location
		move.b	d2,Subtype(a1)		; set subtype

		bsr	LoadSaveFileName	; load save file name
		bsr	LoadSaveFileStuff	; load rest of the save file data

.NoSpecial	lea	SRAM_Slot_Size(a2),a2	; Align to next slot
		addq.b	#4,d2			; align to next VDP setting
		moveq	#2,d0			; interrupt routine 2
		bsr	DelayProgram		; make sure everything will be displayed correctly (Fixes the issue with slot 2 name display)
		dbf	d6,.loop		; loop until 0
		rts

SSF_VDPlist:	dc.l $42040003,$42280003,$48040003,$48280003

; ===========================================================================
; ---------------------------------------------------------------------------
; subroutine to load save file name
; ---------------------------------------------------------------------------

LoadSaveFileName:
		moveq	#(SRAM_Filename_Lenght/2)-1,d0; get amount of repeats
		addi.l	#$840000,d5		; align to next line + 2 spaces
		move.l	d5,$C00004		; setup VDP for transfer
		lea	SRAM_FileName-SRAM_Progress(a2),a1; get the save file name address

.LoadChars	moveq	#0,d1
		move.b	(a1),d1			; get next letter
		beq	.Load0			; if 0, load "?"
		subi.b	#$20,d1			; subtract $20 from letter id
		bpl	.NotSpace		; if still positive, this is proper ASCII character, write to plane
.Load0		move.b	#'?'-$20,d1		; load letter to display

.NotSpace	add.w	d3,d1			; add VRAM address to the letter ID
		move.w	d1,$C00000		; tell VDP to display this tile
		addq.l	#2,a1			; align to next letter
		dbf	d0,.LoadChars		; loop until d0 is 0
		rts

LoadSaveFileStuff:
		move.l	d2,-(sp)		; push d2 to stack
		lea	SRAM_LastLVL-SRAM_Progress(a2),a1; get the save data address

		addi.l  #$1960000,d5            ; align 3 rows down + some spaces
		move.l  d5,$C00004              ; setup VDP for transfer

		moveq	#0,d0			; clear d0
		moveq	#0,d1			; clear d1
		moveq	#0,d2			; clear d2
		addi.w	#$10,d3			; align to "0"

		move.b	$A(a1),d1		; Get our progress counter
		beq	.Display0		; if 0, skip this (Zero Divide error sucks)
		cmpi.b	#100,d1			; is it 100?
		beq	.Display100		; if is, manually display 100

		moveq	#10,d4			; divide by $A
		bsr	.CalcHexToDecToVDP	; display first number

		add.w	d3,d1			; add VRAM setting to d1
		move.w	d1,$C00000		; display second number

		move.l	(sp)+,d2		; pop d2 from stack
		rts

.Display100	subi.l	#$20000,d5		; align 1 tile back
		move.l  d5,$C00004              ; setup VDP for transfer

		moveq	#1,d0			; set d0 to 1 for letter "1"
		add.w	d3,d0			; add VRAM setting to d0
		move.w	d0,$C00000		; write "1" to plane

.Display0	move.w	d3,$C00000		; write "0" to plane
		move.w	d3,$C00000		; do above again
		move.l	(sp)+,d2		; pop d2 from stack
		rts
		
; ===========================================================================
; ---------------------------------------------------------------------------
; subroutine to convert hex to decimal
; ---------------------------------------------------------------------------

.CalcHexToDecToVDP
		move.w	d1,d2		; copy d1
		divu.w	d4,d2		; divide d2 by d4

		move.w	d2,d0		; copy d2
		add.w	d3,d0		; add VRAM setting to d0
		move.w	d0,$C00000	; write to plane

		mulu.w	d4,d2		; multiply d2 by d4
		sub.w	d2,d1		; subtract d2 from d1
		rts

