ControlSaveSelect:
		move.b	$FFFFF605,d4		; get controls
		and.b	#$F,d4			; filter out only D-PAD
		beq.s	Cntrls_End		; if none are pressed, branch

		moveq	#0,d0
		move.b	Spindash_HorizDelay,d0	; get current highlighted item
		lsr.b	#2,d0			; shift bits right by 2 to get correct settings
		move.b	TileHighlightArray(pc,d0),d2; get correct settings
		and.b	d2,d4			; filter out more button presses, to see if you are pressing valid buttons

		moveq	#0,d0
		btst	#0,d4			; check up
		beq.s	.ChkDwn			; if not pressed, branch
		subq.b	#8,Spindash_HorizDelay	; move the selection up
		moveq	#$FFFFFFCD,d0		; Button press sound

.ChkDwn		btst	#1,d4			; check down
		beq.s	.ChkLeft		; if not pressed, branch
		addq.b	#8,Spindash_HorizDelay	; move the selection down
		moveq	#$FFFFFFCD,d0		; Button press sound

.ChkLeft	btst	#2,d4			; check left
		beq.s	.ChkRight		; if not pressed, branch
		subq.b	#4,Spindash_HorizDelay	; move the selection left
		moveq	#$FFFFFFCD,d0		; Button press sound

.ChkRight	btst	#3,d4			; check right
		beq.s	.Reload			; if not pressed, branch
		addq.b	#4,Spindash_HorizDelay	; move the selection right
		moveq	#$FFFFFFCD,d0		; Button press sound

.reload		bsr.w	PlaySound_Special	; play it
		bsr.w	LoadSaveFileFrames	; reload the screen
Cntrls_End:
		rts
; ==============================================================================
; ------------------------------------------------------------------------------
; Subroutine to load ASCII character to the plane
; ------------------------------------------------------------------------------

LoadASCII:
		move.l	d5,$C00004	; setup VDP for transfer

.LoadLetter	moveq	#0,d1
		move.b	(a1)+,d1	; get next letter
		beq	Cntrls_End	; if 0, end transfer
		subi.b	#$20,d1		; subtract $20 from letter id
		bpl	.NotSpace	; if still positive, this is proper ASCII character, write to to VDP
		add.l	#$800000,d5	; othervise, start from next row
		bra	LoadASCII

.NotSpace	add.w	d3,d1		; add VRAM address to the letter ID
		move.w	d1,$C00000	; tell VDP to display this tile
		bra	.LoadLetter	; next letter

; ===========================================================================
; ---------------------------------------------------------------------------
; SRAM copy
; ---------------------------------------------------------------------------

SSF_F_Copy:
		move.b	#1,Player_DoubleJump	; set mode to copy
		move.l	SRAM_SlotID,-(sp)	; save slot ID
SSF_F_Copy2:
		bra.s	SSF_F_move2

; ===========================================================================
; ---------------------------------------------------------------------------
; SRAM move
; ---------------------------------------------------------------------------

SSF_F_Move:
		move.b	#-1,Player_DoubleJump	; set mode to move
		move.l	SRAM_SlotID,-(sp)	; save slot ID
SSF_F_move2:
		lea	SFF_Copy_Mainframe,a1	; get the frame
		bsr	LoadASCII		; load it
		bsr	LoadSaveFileFrames	; reload save file frames
		bsr	SSF_MoveCopy_loop	; do the main loop
		bra	SSF_CheckOk		; perform rest

SSF_MoveCopy_loop:
		moveq	#8,d0			; interrupt routine 8
		bsr	DelayProgram		; VSynch
		btst	#7,$FFFFF605		; is Start button pressed?
		bne	SSF_FM_rts		; if is, branch

		bsr	Runplc_RAM		; run PLC to RAM
		bsr	ControlSaveSelect	; allow controlling of the save file select
		jsr	ObjectsLoad		; load objects
		jsr	BuildSprites		; build sprites
		bra	SSF_MoveCopy_loop	; continue the loop

; ===========================================================================
; ---------------------------------------------------------------------------
; menu to sort out destination and source being same
; ---------------------------------------------------------------------------

SSF_ChkCancel:
		lea	SSF_DestError,a1	; load destination error frame
		move.w	#$24B6,d3		; VRAM address
		move.l	#$41860003,d5		; VDP setup
		bsr	LoadASCII		; load ASCII

		move.b	#0,Player_DJ_Data
		lea	.VDP,a2			; get the VDP setting list
		lea	.Item,a4		; get the item list
		moveq	#1,d6			; set repeat count
		bsr	SSF_DE_Load		; load highlighted letter


.loop		moveq	#2,d0			; interrupt routine 2
		bsr	DelayProgram		; VSynch

		lea	.VDP,a2			; get the VDP setting list
		lea	.Item,a4		; get the item list
		moveq	#1,d6			; set repeat count
		bsr	SSF_ControlTinyMenu	; control the menu
		btst	#7,$FFFFF605		; is Start button pressed?
		beq	.loop			; if not, branch    

		moveq	#0,d0
		add.b	Player_DJ_Data,d0	; add selection to d0 
		add.l	d0,sp			; increment stack pointer

		cmpi.b	#4,d0			; is d0 4?
		beq	SaveSelect_2		; if is, branch out
		bra	SSF_F_Move2		; redo this

.VDP		dcb.l 4,$430C0003
.Item		dc.l SSF_DE_2,SSF_DE_1

; ===========================================================================
; ---------------------------------------------------------------------------
; Routine to control menu with 2 options
; ---------------------------------------------------------------------------

SSF_ControlTinyMenu:
                move.b	$FFFFF605,d0		; get the button presses
		andi.b	#%00001111,d0		; only leave up/down
		beq	Cntrls_End		; if neither are pressed, GTFO
		bchg	#2,Player_DJ_Data	; change selection

SSF_DE_Load:
	     	bra	SSF_FM_HighLight	; load higlighted frame

; ===========================================================================
; ---------------------------------------------------------------------------
; routine to do copy/move functions
; ---------------------------------------------------------------------------

SSF_CheckOk:
		bsr	SSF_GetSaveFileID	; get save file ID
		move.l	(sp),a2			; get source address
		cmpa.l	a1,a2			; are they save?
		beq	SSF_ChkCancel		; if are, branch to destination error

		tst.b	Player_DoubleJump	; was the move mode set?
		bpl	SSF_Prompt		; if not, branch

		lea	SSF_SwitchMove,a1	; frame
		move.w	#$24B6,d3		; VRAM address
		move.l	#$41920003,d5		; VDP setup
		bsr	LoadASCII		; load
		move.b	#0,Player_DJ_Data
		
		lea	SSF_DE_VDP,a2		; get the VDP setting list
		lea	SSF_DE_Item,a4		; get the item list
		moveq	#1,d6			; set repeat count
		bsr	SSF_DE_Load		; load highligted item

.loop           moveq	#2,d0			; interrupt routine 2
		bsr	DelayProgram		; VSynch

                lea	SSF_DE_VDP,a2		; get the VDP setting list
		lea	SSF_DE_Item,a4		; get the item list
		moveq	#1,d6			; set repeat count
		bsr	SSF_ControlTinyMenu	; control the menu

                btst	#7,$FFFFF605		; is Start button pressed?
		beq.s	.loop			; if not, branch

		move.l	SRAM_SlotID,a1		; get destination
		tst.b	Player_DJ_Data		; test selection
		bne	SSF_Prompt		; if 0, branch

	; function switch
		move.l	(sp)+,a2		; get source
		lea	SRAM_FreeSlot,a3	; get free slot to fill in
		moveq	#(SRAM_Slot_Size/8)-1,d0; lenght

.FillSource	movep.l	0(a2),d1		; move source to d1
		movep.l	d1,0(a3)		; move d1 to free slot
		movep.l	0(a1),d1		; move destination to d1
		movep.l	d1,0(a2)		; move d1 to source
		addq.l	#8,a1			; next 8 bytes
		addq.l	#8,a2			; ^
		addq.l	#8,a3			; ^
		dbf	d0,.FillSource		; loop until done
		
		moveq	#(SRAM_Slot_Size/8)-1,d0; lenght
		moveq	#0,d2			; clear d2
		lea	SRAM_FreeSlot,a3	; get data from source address
		move.l	SRAM_SlotID,a1		; get destination
		
.FillDest	movep.l	0(a3),d1		; move source data to d1
		movep.l	d1,0(a1)		; move d1 to destination
		movep.l	d2,0(a3)		; clear extra SRAM space
                addq.l	#8,a1			; next 8 bytes
		addq.l	#8,a3			; ^
		dbf	d0,.FillDest		; loop until done

		bsr	SSF_ClearTails		; clear tails' tail
	;	addq.l	#4,sp			; increment stack pointer
		bra	SaveSelect_2		; go back to save select


SSF_DE_VDP	dcb.l 4,$42980003
SSF_DE_Item	dc.l SFF_SM_Switch,SSF_SM_OverWrite

SSF_P_VDP	dcb.l 4,$43960003
SSF_P_Item	dc.l SSF_PO_NO,SSF_PO_OK

; ===========================================================================
; ---------------------------------------------------------------------------
; routine to prompt the user if save file exists
; ---------------------------------------------------------------------------

SSF_Prompt:
		tst.b	SRAM_Progress(a1)	; is this file started?
		bmi	SSF_NoPrompt		; if not, branch

		lea	SSF_PromptOverwrite,a1	; frame
		move.w	#$24B6,d3		; VRAM address
		move.l	#$418E0003,d5		; VDP setup
		bsr	LoadASCII		; load it
		move.b	#0,Player_DJ_Data

		lea	SSF_P_VDP,a2		; get the VDP setting list
		lea	SSF_P_Item,a4		; get the item list
		moveq	#1,d6			; set repeat count
		bsr	SSF_DE_Load		; load highlighted item
		
.loop           moveq	#2,d0			; interrupt routine 2
		bsr	DelayProgram		; VSynch

                lea	SSF_P_VDP,a2		; get the VDP setting list
		lea	SSF_P_Item,a4		; get the item list
		moveq	#1,d6			; set repeat count
		bsr	SSF_ControlTinyMenu	; control the menu

                btst	#7,$FFFFF605		; is Start button pressed?
		beq	.loop			; if not, branch

                tst.b	Player_DJ_Data		; test selection
		bne	SSF_NoPrompt		; if 0, branch
		addq.l	#4,sp			; increment stack pointer
		bra	SaveSelect_2		; back to save select

SSF_NoPrompt:
		move.l	SRAM_SlotID,a1		; get destination
		move.l	(sp)+,a2		; get source
		moveq	#(SRAM_Slot_Size/8)-1,d0; lenght
		
		tst.b	Player_DoubleJump	; test mode
		bmi	.Move			; if move mode, branch
		
		bsr	.Copy			; copy
	;	addq.l	#4,sp			; increment stack pointer
		bsr	SSF_ClearTails		; clear tails' tail
		bra	SaveSelect_2		; back to save select
		
; ===========================================================================
; ---------------------------------------------------------------------------
; routine to copy existing slot
; ---------------------------------------------------------------------------

.Copy		movep.l	0(a2),d1		; source to d1
		movep.l	d1,0(a1)		; d1 to destination
		addq.l	#8,a1			; increment source
		addq.l	#8,a2			; increment destination
		dbf	d0,.Copy		; copy until d0 = 0
		rts
		
; ===========================================================================
; ---------------------------------------------------------------------------
; routine to move slot (overwrite)
; ---------------------------------------------------------------------------

.Move		bsr	.Copy			; copy
		lea	-SRAM_Slot_Size(a2),a1	; get the proper slot address
		bsr	SSF_ClearTails		; clear tails' tails
		bra	SSF_M_Delete		; Then delete source slot

; ===========================================================================
; ---------------------------------------------------------------------------
; routine to delete a save slot
; ---------------------------------------------------------------------------

SSF_F_Delete:
		move.l	SRAM_SlotID,a1		; get slot ID

SSF_M_Delete:
		moveq	#(SRAM_Slot_Size/8)-1,d0; get amount of repeats
 		moveq 	#-1,d1			; clear d1

.loop		movep.l	d1,0(a1)		; clear 8 bytes
		addq.l	#8,a1			; increment by 8 bytes
		dbf	d0,.loop		; loop until d0 = 0

		move.b	d1,Player_DJ_Data	; clear option
		addq.b	#1,-$FE(a1)		; Fix the character option
		addq.b	#1,-$F4(a1)		; clear completed zones

		moveq	#0,d1
		movep.l	d1,-$F2(a1)		; clear first set of zones
		movep.l	d1,-$EA(a1)		; clear second set of zones

		bra	SaveSelect_2		; ge back to save select
		
; ===========================================================================
; ---------------------------------------------------------------------------
; routine to clear tails' tails animation (to stop the bugged graphics bug
; ---------------------------------------------------------------------------

SSF_ClearTails:
		lea	Object_RAM+Anim_Restart+$140,a3; get first tails' address
		move.b	#-1,(a3)		; clear animation restart flag
		move.b	#-1,$40(a3)		; ^
		move.b	#-1,$80(a3)		; ^
		move.b	#-1,$C0(a3)		; ^
		rts

; ===========================================================================
; ---------------------------------------------------------------------------
; function to choose how to operate save file
; ---------------------------------------------------------------------------

SSF_FileMenu:
	     	moveq	#0,d0
	     	move.b	d0,Player_DJ_Data	; clear cursor position
	     	move.b	Spindash_HorizDelay,d0	; get selected file
	     	move.l	SFF_MenuPoslist(pc,d0),d5; get position
	     	lea	SSF_Menu,a1		; Get menu frame
	     	move.w	#$24B6,d3		; VRAM address
	     	bsr	LoadASCII		; load ASCII
	     	
	     	lea	SFF_MenuPoslist,a2	; get the VDP setting list
		lea	SSF_FM_ItemList,a4	; get the item list
		moveq	#3,d6			; set repeat count
	     	bsr	SSF_FM_HighLight	; load higlighted frame

.loop		moveq	#2,d0			; interrupt routine 2
		bsr	DelayProgram		; VSynch
		bsr.s	ControlFileMenu		; control the menu

		btst	#7,$FFFFF605		; is Start button pressed?
		beq.s	.loop			; if not, branch

		moveq	#0,d0
		move.w	#$24B6,d3		; VRAM address
		move.l	#$40000003,d5		; VDP setup

	     	move.b	Player_DJ_Data,d0	; get selection ID
		move.l	.Index(pc,d0.w),a1	; get the address pointer to a1
		jmp	(a1)			; jump to address pointer

.Index		dc.l SRAMInitialize, SSF_F_Copy, SSF_F_Delete, SSF_F_Move

SFF_MenuPoslist	dc.l $42860003,$42AA0003,$48860003,$48AA0003

; ===========================================================================
; ---------------------------------------------------------------------------
; routine to control the file menu
; ---------------------------------------------------------------------------

ControlFileMenu:
		move.b	$FFFFF605,d4		; get controls
		andi.b	#%00000011,d4		; filter out only up/down
		beq	SSF_FM_rts		; if none are pressed, branch

		addq.b	#4,Player_DJ_Data	; next selection
                andi.b	#%00000001,d4		; filter out only up
		beq	.			; if is not pressed, branch
		subq.b	#8,Player_DJ_Data	; previous selection

.		lea	SFF_MenuPoslist,a2	; get the VDP setting list
		lea	SSF_FM_ItemList,a4	; get the item list
		moveq	#3,d6			; set repeat count


		tst.b	Player_DJ_Data		; test selection
		bpl.s	.1			; if positive, branch
		move.b	#12,Player_DJ_Data	; set to last selection

.1		cmpi.b	#12,Player_DJ_Data	; check if selection #3
		bls.s	SSF_FM_HighLight	; if less than that, branch
		move.b	#0,Player_DJ_Data	; set to selection #0

; ===========================================================================
; ---------------------------------------------------------------------------
; routine to highlight on the save file
; ---------------------------------------------------------------------------

SSF_FM_HighLight:
		moveq	#0,d0
	     	move.b	Spindash_HorizDelay,d0	; get selected file
	     	move.l	(a2,d0),d5		; get VDP setting
 		addi.l	#$840000,d5		; next line + skip 2 spaces


		moveq	#0,d2			; clear d2

.loop		move.l	(a4,d2),a1		; get proper item
		move.w	#$24B6,d3		; VRAM address
		cmp.b	Player_DJ_Data,d2	; is this selection highlighted?
		bne.s	.c			; if not, branch
		move.w	#$44B6,d3		; highlighted tiles

.c		bsr	LoadASCII		; load it
		addi.l	#$800000,d5		; next row
		addq.b	#4,d2			; next item
		dbf	d6,.loop		; loop until 0
SSF_FM_rts:
		rts

SSF_FM_ItemList: dc.l SSF_Play,SSF_Copy,SSF_Delete,SSF_Move
; ===========================================================================
; ---------------------------------------------------------------------------
; subroutine to handle player select
; ---------------------------------------------------------------------------

SSF_SelectChar:
		bsr	LoadSaveFileFrames
		move.w	#$24B6,d3		; VRAM address
		move.l	#$40000003,d5		; VDP setup
		lea	SFF_Mainframe_Clear,a1	; data
		bsr	LoadASCII		; load the main frame

		move.l	#$40100003,d5		; VDP setup
		lea	SSF_PlayerSelect,a1	; data
		bsr	LoadASCII		; load the main frame
                bsr	SFF_LoadPlayerNames
                jsr	ObjectsLoad		; load objects
                jsr	ObjectsLoad		; load objects

		move.b	#$92,$FFFFD7C0		; load player object
		move.b	#16,$FFFFD7C0+Subtype	; setup subtype for VRAM address
		move.l	SRAM_SlotID,$FFFFD7C0+Off3C; save SRAM location

.loop		moveq	#8,d0			; interrupt routine 2
		bsr	DelayProgram		; VSynch
		bsr.s	MovePlayers
		jsr	ObjectsLoad2		; load objects
		jsr	BuildSprites		; build sprites
		btst	#7,$FFFFF605		; is Start button pressed?
		beq.s	.loop			; if is not, branch
SSF_SC_RTS:
		rts

; ===========================================================================
; ---------------------------------------------------------------------------
; Subroutine to control players on save select screen
; ---------------------------------------------------------------------------

MovePlayers:
		subq.b	#1,Player_DJ_Data	; subtract 1 from the wait counter
		bpl.s	SSF_SC_RTS		; if still positive, branch
		addq.b	#1,Player_DJ_Data	; keep negative
		move.b	$FFFFF605,d0		; get the button presses
		andi.b	#%00001111,d0		; only leave D-PAD controls
		beq.s	SSF_SC_RTS		; if none are pressed, GTFO

		move.l	SRAM_SlotID,a2		; get correct slot
		addq.b	#2,SRAM_Char(a2)	; next character ID
		andi.b	#%00000110,d0		; only leave left/down
		beq.s	.Continue		; if none are pressed, GTFO
		subq.b	#4,SRAM_Char(a2)	; next character ID
		
.Continue	tst.b	SRAM_Char(a2)		; test char id
		bpl.s	.ChkMax			; if positive, check max
		move.b	#(CharacterCount-1)*2,SRAM_Char(a2); set the id

.ChkMax		cmpi.b	#CharacterCount*2,SRAM_Char(a2); check if same or more than the max
		blo.s	.OK			; if not, branch
		move.b	#0,SRAM_Char(a2)	; clear id

.OK		move.b	#0,$FFFFD7C0+Routine	; clear routine counter
		jsr	SingleObjLoad		; load object
		bne	.Delay			; if none could be loaded, branch
		move.b	#$27,(a1)		; load "explosion from a destroyed enemy"
		move.b	#6,Routine(a1)		; set suboutine (Eplosion smoke
		move.w	#$A0,X_Pos(a1)		; set position
		move.w	#$2A,Y_Pos(a1)		; ^

.Delay		move.b	#8,Player_DJ_Data	; set wait delay for next char

; ===========================================================================
; ---------------------------------------------------------------------------
; Subroutine to load player name's on save select screen
; ---------------------------------------------------------------------------

SFF_LoadPlayerNames:
		moveq	#0,d0
		move.l	SRAM_SlotID,a2		; get correct slot
		move.b	SRAM_Char(a2),d0	; get the character id
		add.w	d0,d0			; multiply d0
		move.l	SFF_ASCIITextList(pc,d0),a1; fetch correct item

		move.l	#$44100003,d5		; VDP setup
		move.w	#$24B6,d3		; VRAM address
		bra	LoadASCII		; load the main frame
		
SFF_ASCIITextList:	dc.l SSF_PS_Sonic, SSF_PS_Tails, SSF_PS_Knuckles, SFF_PS_IllegalValue
; ==============================================================================
; ------------------------------------------------------------------------------
; Subroutine to allow the user to define save file name
; ------------------------------------------------------------------------------

SSF_Inputname:
		move.w	#$24B6,d3		; VRAM address
		move.l	#$42960003,d5		; VDP setup
		lea	SFF_NameSel,a1		; data
		bsr	LoadASCII		; load the main frame

		move.l	#$47920003,d5		; VDP setup
		lea	SFF_NameSel_2,a1	; data
		bsr	LoadASCII		; load the secondary frame

		move.b	#0,Spindash_HorizDelay+1; clear the pointer for cursor position
		bsr	SFF_Clearname		; clear the save file name (to ensure no issues come up with it)

.loop		moveq	#8,d0			; interrupt routine 8
		bsr	DelayProgram		; VSynch
		addq.b	#1,Player_DJ_Data-1	; cycle the counter
		bsr	ControlNameSelect	; run control routines
		bsr	SFF_LoadName		; reload the lettera
		btst	#7,$FFFFF605		; is Start button pressed?
		beq	.loop			; if is not, branch
		
SSF_SelNameEnd:
		rts

; ===========================================================================
; ---------------------------------------------------------------------------
; subroutine to control name the letter highlights and change letter ids
; ---------------------------------------------------------------------------

ControlNameSelect:
		move.b	$FFFFF605,d1		; get pressed button state
		andi.b	#%00001100,d1		; filter out all but Left/Right
		beq	.NoPress		; if neither is pressed, branch
		moveq	#2,d0			; move 2 to d0
		andi.b	#%00000100,d1		; filter out left
		beq	.add			; if left was not being pressed, branch
		moveq	#-2,d0			; move negative 2 to d0

.add		add.b	d0,Spindash_HorizDelay+1; add it to the selected letter count
		bpl	.pos			; if positive, branch
		move.b	#SRAM_Filename_Lenght-2,Spindash_HorizDelay+1; othervise, reset the counter position
		rts

.pos		cmpi.b	#SRAM_Filename_Lenght,Spindash_HorizDelay+1; check the counter
		blo	.end			; if it is still in correct area, branch
		move.b	#0,Spindash_HorizDelay+1; clear the counter
.end		rts

.NoPress	move.b	$FFFFF605,d2		; get pressed buttons again
		andi.b	#%00000011,d2		; is up/down pressed?
		beq	SSF_SelNameEnd		; if not, branch

		move.l	SRAM_SlotID,a2		; get current slot
		lea	SRAM_FileName(a2),a2	; get the filename to a2

		moveq	#0,d0
		move.b	Spindash_HorizDelay+1,d0; get our selected letter
		add.w	d0,a2			; Save proper location to a2

		moveq	#1,d1			; move up/down by 1
		btst	#5,$FFFFF604		; is C being pressed
		beq	.NoC			; if not, branch
		moveq	#2,d1			; move up/down by 2

.NoC		btst	#4,$FFFFF604		; is B being pressed
		beq	.NoB			; if not, branch
		addq.b	#4,d1			; increase the selection increase by 4
		bclr	#0,d1			; clear bit 0 of d1 (To jump even distances only)


.NoB		btst	#6,$FFFFF604		; is A being pressed
		beq	.NoA			; if not, branch
		addi.b	#8,d1			; increase the selection increase by 8
		bclr	#0,d1			; clear bit 0 of d1 (To jump even distances only)

.NoA		andi.b	#%00000010,d2		; are we pressing down?
		bne	.SaveChar		; if yes, branch
		neg.b	d1			; positive to negative

.SaveChar	add.b	d1,(a2)			; add it to letter id
		bclr	#7,(a2)			; clear bit 7 (to prevent negative values) ($7F is the id of the last letter)
		beq	.Notnegative		; if it was cleared already, branch
		addi.b	#$20,(a2)		; align to proper letter area
		rts

.NotNegative	cmpi.b	#$20,(a2)		; is the letter id $20?
		bhs	.rts			; if more than that, branch
		addi.b	#$60,(a2)		; add $60 to the id (To get to last letters in the proper area)
.rts		rts

