; ---------------------------------------------------------------------------
; subroutine to clear save file name
; ---------------------------------------------------------------------------

SFF_Clearname:
		move.l	SRAM_SlotID,a2		; get correct slot
		lea	SRAM_FileName(a2),a2	; get the filename address
		moveq	#(SRAM_Filename_Lenght/2)-1,d4; set amount of repeats

.Loop		move.b	#$3F,(a2)		; fill with "?"
		addq.l	#2,a2			; align to next slot
		dbf	d4,.Loop		; loop until done
		
; ===========================================================================
; ---------------------------------------------------------------------------
; subroutine to load save file name to plane
; ---------------------------------------------------------------------------

SFF_LoadName:
		moveq	#0,d2
		move.l	SRAM_SlotID,a2		; get correct slot
		lea	SRAM_FileName(a2),a2	; get the filename address
		move.l	#$439A0003,$C00004	; VDP setup
		moveq	#(SRAM_Filename_Lenght/2)-1,d4; set amount of repeats
		moveq	#0,d0
		move.b	Player_DJ_Data-1,d0	; get the letter flash counter
		andi.b	#$10,d0			; filter out all bits but bit 4

.Loop		moveq	#0,d1
		move.b	(a2),d1			; get the letter
		subi.w	#$20,d1			; sub $20 from the id

.Not?		move.w	#$24B6,d3		; VRAM address
		cmp.b	Spindash_HorizDelay+1,d2; is this letter being highlighted?
		bne	.Is0			; if not, branch
		tst.b	d0			; is d0 0? (Flash counter bit 4)
		beq	.Is0			; if is, branch
		move.w	#$44B6,d3		; VRAM address

.Is0		add.w	d3,d1			; add VRAM address to the letter ID
		move.w	d1,$C00000		; tell VDP to display this tile

		lea	2(a2),a2		; get next slot
		addq.b	#2,d2			; next item
		dbf	d4,.Loop		; loop until 0
		rts

