		cmpa.w	#$D7C0,a0
		beq	.
		addq.b	#2,Routine(a0)		; next routine

.		move.w	#$5FF,$1C(a0)		; set animation
		move.b	#3,Priority(a0)

                move.l	Off3C(a0),a1		; get SRAM location for current selection
                moveq	#0,d0
		move.b	SRAM_Char(a1),d0	; move character id to d0
		lsl.w	#3,d0			; shift bits left 3 times (multiply by 8)
		lea	SSF_PDA+4(pc,d0.w),a3	; get proper address
