; ---------------------------------------------------------------------------
; Subroutine to load players pallets
; ---------------------------------------------------------------------------

LoadPlayerPallets:
		moveq	#0,d0
		move.b	Current_Character,d0	; get char
		move.w	CharPalList(pc,d0.w),d0	; get pallet
		lea	CharPalList(pc,d0.w),a1	; put to a1

                cmpi.b	#7,$FFFFE10		; is overworld?
                bne	.NoOW			; if isn't continue
                lea	Pal_Sonic,a1		; We want to load Sonic's pallet only

.NoOW		moveq	#0,d0
		move.b	Time_DayNight,d0	; get time ID
		lsl.w	#5,d0			; multiply by 32
		adda.w	d0,a1			; add to a1

		moveq	#7,d0			; one line
		bsr	Loc_Pal			; load pallets
		cmpi.b	#1,$FFFFFE10		; is LZ?
		bne	.rts			; nope?
		
                moveq	#0,d0
		move.b	Current_Character,d0	; get char
		move.w	CharPalListUW(pc,d0.w),d0; get underwater pallet
		lea	CharPalListUW(pc,d0.w),a1; put to a1

		moveq	#7,d0			; one line
		lea	(a3),a2			; put water pallet to a2
		bsr	Loc_Pal			; load it
.rts		cmpi.b	#3,$FFFFFE11		; is this overworld act?
		bne	.end			; if not, branch
		move.w	#$484,$FFFFFB1E		; line 1, entry $F -> Green
.end		rts

