; ---------------------------------------------------------------------------
; Object 15 - swinging platforms (GHZ, MZ, SLZ)
;	    - spiked ball on a chain (SBZ)
; ---------------------------------------------------------------------------

Obj15:					; XREF: Obj_Index
		moveq	#0,d0
		move.b	Routine(a0),d0
		move.w	Obj15_Index(pc,d0.w),d1
		jmp	Obj15_Index(pc,d1.w)
; ===========================================================================
Obj15_Index:	dc.w Obj15_Main-Obj15_Index, Obj15_SetSolid-Obj15_Index
		dc.w Obj15_Action2-Obj15_Index,	Obj15_Delete-Obj15_Index
		dc.w Obj15_Delete-Obj15_Index, Obj15_Display-Obj15_Index
		dc.w Obj15_Action-Obj15_Index
; ===========================================================================

Obj15_Main:				; XREF: Obj15_Index
		addq.b	#2,Routine(a0)
		move.l	#Map_obj15,Mappings_Offset(a0) ; GHZ and MZ specific code
		move.w	#$4380,Art_Tile(a0)
		move.b	#4,Render_Flags(a0)
		move.b	#3,Priority(a0)
		move.b	#$18,X_Visible(a0)
		move.b	#8,Y_Radius(a0)
		move.w	Y_Pos(a0),Off38(a0)
		move.w	X_pos(a0),Off3A(a0)
		cmpi.b	#3,($FFFFFE10).w ; check if level is SLZ
		bne.s	Obj15_NotSLZ
		move.l	#Map_obj15a,Mappings_Offset(a0) ; SLZ	specific code
		move.w	#$43DC,Art_Tile(a0)
		move.b	#$20,X_Visible(a0)
		move.b	#$10,Y_Radius(a0)
		move.b	#$99,Coll(a0)

Obj15_NotSLZ:
		cmpi.b	#5,($FFFFFE10).w ; check if level is SBZ
		bne.s	Obj15_SetLength
		move.l	#Map_obj15b,Mappings_Offset(a0) ; SBZ	specific code
		move.w	#$391,Art_Tile(a0)
		move.b	#$18,X_Visible(a0)
		move.b	#$18,Y_Radius(a0)
		move.b	#$86,Coll(a0)
		move.b	#$C,Routine(a0)

Obj15_SetLength:
		move.b	0(a0),d4
		moveq	#0,d1
		lea	Subtype(a0),a2	; move chain length to a2
		move.b	(a2),d1		; move a2 to d1
		move.w	d1,-(sp)
		andi.w	#$F,d1
		move.b	#0,(a2)+
		move.w	d1,d3
		lsl.w	#4,d3
		addq.b	#8,d3
		move.b	d3,Off3C(a0)
		subq.b	#8,d3
		tst.b	Anim_Frame(a0)
		beq.s	Obj15_MakeChain
		addq.b	#8,d3
		subq.w	#1,d1

Obj15_MakeChain:
		bsr.w	SingleObjLoad
		bne.s	loc_7A92
		addq.b	#1,Subtype(a0)
		move.w	a1,d5
		subi.w	#-$3000,d5
		lsr.w	#6,d5
		andi.w	#$7F,d5
		move.b	d5,(a2)+
		move.b	#$A,Routine(a1)
		move.b	d4,0(a1)	; load swinging	object
		move.l	Mappings_Offset(a0),Mappings_Offset(a1)
		move.w	Art_Tile(a0),Art_Tile(a1)
		bclr	#6,Art_Tile(a1)
		move.b	#4,Render_Flags(a1)
		move.b	#4,Priority(a1)
		move.b	#8,X_Visible(a1)
		move.b	#1,Anim_Frame(a1)
		move.b	d3,Off3C(a1)
		subi.b	#$10,d3
		bcc.s	loc_7A8E
		move.b	#2,Anim_Frame(a1)
		move.b	#3,Priority(a1)
		bset	#6,Art_Tile(a1)

loc_7A8E:
		dbf	d1,Obj15_MakeChain ; repeat d1 times (chain length)

loc_7A92:
		move.w	a0,d5
		subi.w	#-$3000,d5
		lsr.w	#6,d5
		andi.w	#$7F,d5
		move.b	d5,(a2)+
		move.w	#$4080,Angle(a0)
		move.w	#-$200,Off3E(a0)
		move.w	(sp)+,d1
		btst	#4,d1		; is object type $8X ?
		beq.s	loc_7AD4	; if not, branch
		move.l	#Map_obj48,Mappings_Offset(a0) ; use GHZ ball	mappings
		move.w	#$43AA,Art_Tile(a0)
		move.b	#1,Anim_Frame(a0)
		move.b	#2,Priority(a0)
		move.b	#$81,Coll(a0)	; make object hurt when	touched

loc_7AD4:
		cmpi.b	#5,($FFFFFE10).w ; is zone SBZ?
		beq.s	Obj15_Action	; if yes, branch

Obj15_SetSolid:				; XREF: Obj15_Index
		moveq	#0,d1
		move.b	X_Visible(a0),d1
		moveq	#0,d3
		move.b	Y_Radius(a0),d3
		bsr.w	Obj15_Solid

Obj15_Action:				; XREF: Obj15_Index
		bsr.w	Obj15_Move
		bsr.w	DisplaySprite
		bra.w	Obj15_ChkDel
; ===========================================================================

Obj15_Action2:				; XREF: Obj15_Index
		moveq	#0,d1
		move.b	X_Visible(a0),d1
		bsr.w	ExitPlatform
		move.w	X_pos(a0),-(sp)
		bsr.w	Obj15_Move
		move.w	(sp)+,d2
		moveq	#0,d3
		move.b	Y_Radius(a0),d3
		addq.b	#1,d3
		bsr.w	MvSonicOnPtfm
		bsr.w	DisplaySprite
		bra.w	Obj15_ChkDel

		rts

; ---------------------------------------------------------------------------
; Subroutine to	change Sonic's position with a platform
; ---------------------------------------------------------------------------

; ||||||||||||||| S U B	R O U T	I N E |||||||||||||||||||||||||||||||||||||||


MvSonicOnPtfm:
		lea	Object_RAM,a1
		move.w	Y_Pos(a0),d0
		sub.w	d3,d0
		bra.s	MvSonic2
; End of function MvSonicOnPtfm

; ---------------------------------------------------------------------------
; Subroutine to	change Sonic's position with a platform
; ---------------------------------------------------------------------------

; ||||||||||||||| S U B	R O U T	I N E |||||||||||||||||||||||||||||||||||||||


MvSonicOnPtfm2:
		lea	Object_RAM,a1
		move.w	Y_Pos(a0),d0
		subi.w	#9,d0

MvSonic2:
		tst.b	($FFFFF7C8).w
		bmi.s	locret_7B62
		cmpi.b	#6,Object_RAM+Routine
		bcc.s	locret_7B62
		tst.w	($FFFFFE08).w
		bne.s	locret_7B62
		moveq	#0,d1
		move.b	Y_Radius(a1),d1
		sub.w	d1,d0
		move.w	d0,Y_Pos(a1)
		sub.w	X_pos(a0),d2
		sub.w	d2,X_pos(a1)

locret_7B62:
		rts	
; End of function MvSonicOnPtfm2


; ||||||||||||||| S U B	R O U T	I N E |||||||||||||||||||||||||||||||||||||||


Obj15_Move:				; XREF: Obj15_Action; Obj15_Action2
		move.b	($FFFFFE78).w,d0
		move.w	#$80,d1
		btst	#0,Status(a0)
		beq.s	loc_7B78
		neg.w	d0
		add.w	d1,d0

loc_7B78:
		bra.s	Obj15_Move2
; End of function Obj15_Move


; ||||||||||||||| S U B	R O U T	I N E |||||||||||||||||||||||||||||||||||||||


Obj48_Move:				; XREF: Obj48_Display2
		tst.b	Off3D(a0)
		bne.s	loc_7B9C
		move.w	Off3E(a0),d0
		addq.w	#8,d0
		move.w	d0,Off3E(a0)
		add.w	d0,Angle(a0)
		cmpi.w	#$200,d0
		bne.s	loc_7BB6
		move.b	#1,Off3D(a0)
		bra.s	loc_7BB6
; ===========================================================================

loc_7B9C:
		move.w	Off3E(a0),d0
		subq.w	#8,d0
		move.w	d0,Off3E(a0)
		add.w	d0,Angle(a0)
		cmpi.w	#-$200,d0
		bne.s	loc_7BB6
		move.b	#0,Off3D(a0)

loc_7BB6:
		move.b	Angle(a0),d0
; End of function Obj48_Move


; ||||||||||||||| S U B	R O U T	I N E |||||||||||||||||||||||||||||||||||||||


Obj15_Move2:				; XREF: Obj15_Move; Obj48_Display
		bsr.w	CalcSine
		move.w	Off38(a0),d2
		move.w	Off3A(a0),d3
		lea	Subtype(a0),a2
		moveq	#0,d6
		move.b	(a2)+,d6

loc_7BCE:
		moveq	#0,d4
		move.b	(a2)+,d4
		lsl.w	#6,d4
		addi.l	#$FFD000,d4
		movea.l	d4,a1
		moveq	#0,d4
		move.b	Off3C(a1),d4
		move.l	d4,d5
		muls.w	d0,d4
		asr.l	#8,d4
		muls.w	d1,d5
		asr.l	#8,d5
		add.w	d2,d4
		add.w	d3,d5
		move.w	d4,Y_Pos(a1)
		move.w	d5,X_pos(a1)
		dbf	d6,loc_7BCE
		rts	
; End of function Obj15_Move2

; ===========================================================================

Obj15_ChkDel:				; XREF: Obj15_Action; Obj15_Action2
		move.w	Off3A(a0),d0
		andi.w	#$FF80,d0
		move.w	($FFFFF700).w,d1
		subi.w	#$80,d1
		andi.w	#$FF80,d1
		sub.w	d1,d0
		cmpi.w	#$280,d0
		bhi.w	Obj15_DelAll
		rts	
; ===========================================================================

Obj15_DelAll:				; XREF: Obj15_ChkDel
		moveq	#0,d2
		lea	Subtype(a0),a2
		move.b	(a2)+,d2

Obj15_DelLoop:
		moveq	#0,d0
		move.b	(a2)+,d0
		lsl.w	#6,d0
		addi.l	#$FFD000,d0
		movea.l	d0,a1
		bsr.w	DeleteObject2
		dbf	d2,Obj15_DelLoop ; repeat for length of	chain
		rts	
; ===========================================================================

Obj15_Delete:				; XREF: Obj15_Index
		bsr.w	DeleteObject
		rts	
; ===========================================================================

Obj15_Display:				; XREF: Obj15_Index
		bra.w	DisplaySprite
