Obj25_Main:				; XREF: Obj25_Index
		tst.b	$3F(a0)		; is debug mode active? 
		bne.s	Obj25_MakeRingsD ; if it is, branch away
		lea	($FFFFFC00).w,a2
		moveq	#0,d0
