		move.b	#1,Priority(a0)
		bsr.w	CollectRing
		tst.b	Off3F(a0) ; was this ring spawned in debug mode?
		bne.s	Obj25_Sparkle ; if it was, branch away
		lea	($FFFFFC00).w,a2
		moveq	#0,d0
