		move.b  #-1,Off3F(a0)		; ring timer = $FF
		move.b	#-1,$FFFFFEC6		; general ring timer = $FF
		move.b	#2,Routine(a0)		; routine number 2

                lea	($FFFFFC00).w,a2	; get respawn table
		moveq	#0,d0
		move.b	Respawn(a0),d0		; get respawn index
		move.b	off34(a0),d1		; get objects $34
		bset	d1,2(a2,d0.w)		; set as collected
		
		move.b	#8,Y_Radius(a0)		; set to correct size
		move.b	#8,X_Radius(a0)		; ^
Obj37_rts:
		rts
