		dc.w Obj4B_Animate-Obj4B_Index
		dc.w Obj4B_Collect-Obj4B_Index
		dc.w Obj4B_Delete-Obj4B_Index
; ===========================================================================

Obj4B_Main:				; XREF: Obj4B_Index
		move.l	#Map_obj4B,Mappings_Offset(a0)
		move.w	#$2400,Art_Tile(a0)
		ori.b	#4,Render_Flags(a0)
		move.b	#$40,X_Visible(a0)
		move.b	#$52,Coll(a0)
		tst.b	Render_Flags(a0)
		bpl	Obj4B_Animate
		cmpi.b  #7,$FFFFFe10
		beq	Obj4B_LoadLocks
		cmpi.b	#3,$FFFFFe11
		beq	Obj4B_LoadLocks
		cmpi.b	#6,($FFFFFE57).w ; do you have 6 emeralds?
		beq	Obj4B_Delete	; if yes, branch
		cmpi.w	#50,($FFFFFE20).w ; do you have	at least 50 rings?
		bcc	Obj4B_Okay	; if yes, branch
		rts	
; ===========================================================================

Obj4B_LoadLocks:
		moveq	#0,d1
		moveq	#0,d2
		move.b	subtype(a0),d1		; get subtype of the object
		cmpi.b	#7,$FFFFFE10		; is this overworld?
		bne	Obj4B_NormLvl		; if not, branch

		cmpi.b	#6,d1			; is subtype 6?
		beq	Obj4B_Okay		; if is, branch
                cmpi.b	#9,d1			; is subtype 9?
		beq	Obj4B_Okay		; if is, branch

		bsr	SingleObjLoad		; load an object
		bne	Obj4B_Okay		; if the object could not be loaded, branch
		btst	d1,SRAM_ZoneComplete	; check if zone matching object subtype is beaten
		bne	Obj4B_LoadObject	; if is, branch away
		moveq	#4,d2			; set zone to be open
		tst.b	subtype(a0)		; check subtype
		beq	Obj4B_LoadObject	; if 0, branch
		subq.b	#1,d1			; subtract 1 from d0

                btst	d1,SRAM_ZoneComplete	; check if last zone was is beaten
		bne	Obj4B_LoadObject	; if is, branch away
		move.b	#0,Coll(a0)		; stop colliding
		moveq	#8,d2			; set to locked zone
		bra	Obj4B_LoadObject

Obj4B_NormLvl:
		cmpi.b	#4,d1			; is subtype 4?
		beq	Obj4B_Okay		; if is, branch
                bsr	SingleObjLoad		; load an object
		bne	Obj4B_Okay		; if the object could not be loaded, branch

		moveq	#0,d0
		move.b	$FFFFFE10,d0		; get zone id
		add.w	d0,d0			; multiply by 2 (To get proper SRAM address
		movea.l	SRAM_SlotID,a2		; get slot ID we are using
		adda.w	#$C,a2			; add $C to a2
		lea	(a2,d0.w),a2		; Add zone ID*2 to a2

		btst	d1,(a2)			; is bit set?
		bne	Obj4B_LoadObject	; if is, branch
		moveq	#4,d2			; set to open

Obj4B_LoadObject:
		move.b	#$91,(a1)		; set obj id
		move.w	#$4E3,Art_Tile(a1)	; set Art_Tile
		add.b	d2,Art_Tile+1(a1)	; add d2 to art tile
		move.b	#4,render_flags(a1)	; set render
		move.b	#16,X_Visible(a1)	; set visiblity lenght
		move.l	#Map_Locks,Mappings(a1)	; mappings
		move.w	X_pos(a0),X_pos(a1)	; pos
		move.w	Y_Pos(a0),Y_Pos(a1)	; ^
		addq.w	#8,X_pos(a1)		; move to right a bit
		move.b	#2,Priority(a1)		; set priority

Obj4B_Okay:				; XREF: Obj4B_Main
		addq.b	#2,Routine(a0)
		move.b	#3,Priority(a0)
		move.w	#$C40,($FFFFF7BE).w

Obj4B_Animate:				; XREF: Obj4B_Index
		move.b	($FFFFFEC3).w,Anim_Frame(a0)
		move.w	X_pos(a0),d0
		andi.w	#$FF80,d0
		move.w	($FFFFF700).w,d1
		subi.w	#$80,d1
		andi.w	#$FF80,d1
		sub.w	d1,d0
		cmpi.w	#$280,d0
		bhi.w	DeleteObject
		bra.w	DisplaySprite
; ===========================================================================

Obj4B_Collect:				; XREF: Obj4B_Index
		subq.b	#2,Routine(a0)
		move.b	#0,Coll(a0)
		bsr.w	SingleObjLoad
		bne	Obj4B_PlaySnd
		move.b	#$7C,(a1)	; load giant ring flash	object
		move.w	X_pos(a0),X_pos(a1)
		move.w	Y_Pos(a0),Y_Pos(a1)
		move.l	a0,Off3C(a1)
		move.b  Subtype(a0),Subtype(a1); put the subtype of this object to the flash object
		move.w	Object_RAM+X_Pos,d0
		cmp.w	X_pos(a0),d0	; has Sonic come from the left?
		bcs.s	Obj4B_PlaySnd	; if yes, branch
		bset	#0,Render_Flags(a1)	; reverse flash	object2
Obj4B_PlaySnd:
		move.w	#$C3,d0
		jsr	PlaySound_Special ;	play giant ring	sound
		bra.s	Obj4B_Animate
; ===========================================================================

Obj4B_Delete:				; XREF: Obj4B_Index
		bra.w	DeleteObject
