Obj7C_Delete:	
		cmpi.b  #7,$FFFFFe10
		beq	.7
		cmpi.b  #3,($FFFFFe11).w; is this act 4?
		bne  	DeleteObject	; if not, delete the object
.7
		moveq	#0,d0
		move.b	Subtype(a0),d0	; move subtype to d0
		bsr	.CheckZone	; get the right zone
                move.w	#1,($FFFFFE02).w ; restart level
                move.b	#0,($FFFFFE30).w ; clear lamppost count
                rts

.CheckZone	cmpi.b	#7,($FFFFFe10).w; check if this is overworld
		bne	NormalLevel	; if not, branch away
		; overworld
		cmpi.b	#7,d0		; is d0 = 7?
		beq.s	.FZ		; if is, go to final zone
		cmpi.b	#6,d0		; is d0 = 6?
		beq.s	Lvl_ow		; if is, go back overworld
		cmpi.b	#9,d0		; is d0 = 9?
		beq.s	Lvl_options	; if is, go to options zone
		move.b	d0,($FFFFFe10).w	; otherwise move d0 to zone number
		rts
.FZ		move.w	#$502,($FFFFFe10).w; FZ
Lvl_rts:
		rts
Lvl_options:
	    	move.w	#$702,($FFFFFE10).w; options zone
		rts
Lvl_ow:
       		move.w	#$703,($FFFFFE10).w; overworld
		rts
NormalLevel:
		cmpi.b	#4,d0	; is d0 = 4?
		beq.s	Lvl_ow	; if is, go to overworld
		move.b	d0,$FFFFFe11	; otherwise move d0 to act number
		rts
