		tst.w	Y_Vel(a0)		; is object moving?
		bpl.w	Obj2E_ChkEggman	; if not, branch
		bsr.w	SpeedToPos
		addi.w	#$18,Y_Vel(a0)	; reduce object	speed
		rts	
