                move.b  #0,Render_Flags(a1)
                move.b  #0,Priority(a1)
                move.b  #2,Routine(a1)      ; routine 4
                move.w  #60,Anim_Dur(a1)     ; set time delay to 1 second
                lea     $40(a1),a1      ; next object
