; ===========================================================================
; Dynamic pattern load cues
;
; How it works: First byte is how many requests to give for the DPLC, or how many words it has
; then each words give the art info. In this format: YXXX. Y = How many tiles-1. X = the starting tile
;
DPLC_Obj34:     dc.w ACT1-DPLC_Obj34    ; ACT x
                dc.w ACT2-DPLC_Obj34    ;
                dc.w ACT3-DPLC_Obj34    ;
                dc.w ACTNO-DPLC_Obj34    ;
                dc.w ACT1-DPLC_Obj34    ;
                dc.w ACT2-DPLC_Obj34    ;
                dc.w ACT3-DPLC_Obj34    ;
                dc.w ACTNO-DPLC_Obj34    ;
                dc.w ACTNO-DPLC_Obj34   ; empty
                dc.w ACTOval-DPLC_Obj34 ; ACT, ZONE and oval
                dc.w Oval-DPLC_Obj34    ; ZONE and oval
                dc.w BONU-DPLC_Obj34    ; BONU, ACT and oval
                dc.w SGT-DPLC_Obj34     ; SONIC GOT THROUH text
                dc.w FZ-DPLC_Obj34      ; FINAL text
                dc.w GHZ-DPLC_Obj34     ; Zone texts
                dc.w LZ-DPLC_Obj34      ;
                dc.w MZ-DPLC_Obj34      ;
                dc.w SLZ-DPLC_Obj34     ;
                dc.w SYZ-DPLC_Obj34     ;
                dc.w SBZ-DPLC_Obj34     ;
                dc.w Ow-DPLC_Obj34	;
                dc.w Ow-DPLC_Obj34	;
                dc.b 0  ; pad to next odd address (next line we will get an even address)
; ------------------------------------------------------------------------------------------------------------
; Act number DPLC's. 6 tiles, from $40 to $46 from the objects normal VRAM space
; ------------------------------------------------------------------------------------------------------------
ACTNO:          dc.b 1; (space)
                dc.w $508A
                dc.b 0  ; pad to next odd address
ACT1:           dc.b 2; 1  (space)
                dc.w $2057, $208A
                dc.b 0  ; pad to next odd address
ACT2:           dc.b 1; 2
                dc.w $505A
                dc.b 0  ; pad to next odd address
ACT3:           dc.b 1; 3
                dc.w $5060
                dc.b 0  ; pad to next odd address 
; ------------------------------------------------------------------------------------------------------------
; Misc DPLC's. $2E tiles, from $47 to $75 from the objects normal VRAM space
; ------------------------------------------------------------------------------------------------------------
ACTOval:        dc.b 4;      ACT      Oval
                dc.w $F08A, $2053, $F070, $B080
                dc.b 0  ; pad to next odd address
Oval:           dc.b 4;     no ACT     oval
                dc.w $F08A, $208A, $F070, $B080
                dc.b 0  ; pad to next odd address
BONU:           dc.b 5; -   BONU    ACT       oval
                dc.w $508A, $9066, $2053, $F070, $B080
                dc.b 0  ; pad to next odd address
; ------------------------------------------------------------------------------------------------------------
; Zone texts:   Max of 32 "half" characters (You could fit 32 I's or 16 normal letters),
;               and must always be 64 tiles ($3F tiles) used to be in correct VRAM position for next transfers
;               Always give 1 more request for unknown reason
;               from 0 to $3F from the objects normal VRAM space
; ------------------------------------------------------------------------------------------------------------
GHZ:            dc.b 12; To next line G     R      E      E      N   (Space)  HI      L      L
                dc.w $F08A, $B08A, $3018, $303A, $3010, $3010, $302E, $108A, $501C, $3026, $3026
                dc.b 0  ; pad to next odd address
LZ:             dc.b 14;      HI     D      D      E      N   (Space)   P      A      L      A      C      E
                dc.w $108A, $501C, $300C, $300C, $3010, $302E, $F08A, $3036, $3000, $3026, $3000, $3008, $3010
                dc.b 0  ; pad to next odd address
MZ:             dc.b 10; To next line       M      A      R      B      L      E
                dc.w $F08A, $F08A, $708A, $302A, $3000, $303A, $3004, $3026, $3010
                dc.b 0  ; pad to next odd address
SLZ:            dc.b 13; To next line D     I      S      C      O   (Space)   N      I     GH      T
                dc.w $F08A, $908A, $300C, $1020, $303E, $3008, $3032, $108A, $302E, $1020, $7018, $3042
                dc.b 0  ; pad to next odd address
SYZ:            dc.b 13; To next line S     PR     I      N      G   (space)   Y      A      R      D
                dc.w $F08A, $708A, $303E, $7036, $1020, $302E, $3018, $108A, $304A, $3000, $303A, $300C
                dc.b 0  ; pad to next odd address
SBZ:            dc.b 13;      M      E      T      A      L      (Space)       Z      O      N      E
                dc.w $308A, $302A, $3010, $3042, $3000, $3026, $F08A, $708A, $304E, $3032, $302E, $3010
                dc.b 0  ; pad to next odd address
OW:		dc.b 13; To next line 0     V      E      R   (space)   W      O      R      L      D
		dc.w $F08A, $908A, $3032, $309A, $3010, $303A, $108A, $309E, $3032, $303A, $3026, $300C
		dc.b 0  ; pad to next odd address
FZ:             dc.b 14;      F      I      N      A      L   (space)    B     A      T      T      L      E
                dc.w $508A, $3014, $1020, $302E, $3000, $3026, $F08A, $3004, $3000, $3042, $3042, $3026, $3010
                dc.b 0  ; pad to next odd address
SGT:            dc.b 18; S    O      N      I      C   (space)   G      O      T      H      R      O      U      G      H      T   (Make sure VRAM position is in sync)
                dc.w $303E, $3032, $302E, $1020, $3008, $108A, $3018, $3032, $3042, $301C, $303A, $3032, $3046, $3018, $301C, $3042, $308A
; ---------------------------------------------------------------------------
; Title card configuration data
;
; Only first row is used now due to the tutorial, rest can be deleted
;
;
; Format:
; 4 bytes per item (YYYY XXXX)
; 4 items per level (GREEN HILL, ZONE, ACT X, oval)
; ---------------------------------------------------------------------------
Obj34_ConData:  dc.w 0, $120, $FEFC, $13C, $414, $154, $214, $154 ; GHZ
                dc.w 0, $120, $FEF4, $134, $40C, $14C, $20C, $14C ; LZ
                dc.w 0, $120, $FEE0, $120, $3F8, $138, $1F8, $138 ; MZ
                dc.w 0, $120, $FEFC, $13C, $414, $154, $214, $154 ; SLZ
                dc.w 0, $120, $FF04, $144, $41C, $15C, $21C, $15C ; SYZ
                dc.w 0, $120, $FF04, $144, $41C, $15C, $21C, $15C ; SBZ
                dc.w 0, $120, $FEE4, $124, $3EC, $3EC, $1EC, $12C ; FZ
; ===========================================================================
; load DPLC's
; ---------------------------------------------------------------------------
; Obj34 - Zone name, act id
; ---------------------------------------------------------------------------
Obj34_LoadDPLC:
                lea     DPLC_Obj34,a2           ; get the DPLC
                move.l  #Nem_Titlecard,d6       ; art
                move.w  #$B000,d4               ; VRAM address
                moveq   #0,d0
                move.b  ($FFFFFE10).w,d0        ; DPLC: ZONE x
                addi.b  #GHZ_DPLC,d0            ; DPLC: Get proper zone id 
                cmpi.w  #$502,($FFFFFE10).w     ; FZ?
                bne.s   .notFZ                  ; nope?
                move.w  #GHZ_DPLC-1,d0          ; DPLC: FZ
.notFZ		jsr     Load_DPLC2               ; load the DPLC
                lea     DPLC_Obj34,a2           ; get the DPLC
                move.l  #Nem_Titlecard,d6       ; art
                moveq   #0,d0
                move.b  ($FFFFFE11).w,d0        ; DPLC: ACT x
		cmpi.w  #$502,($FFFFFE10).w     ; FZ?
                bne.s 	.notFZ_2                ; nope?
                moveq   #Act_Count,d0           ; DPLC: ACTNO
.notFZ_2        jsr     Load_DPLC2               ; load the DPLC 
                lea     DPLC_Obj34,a2           ; get the DPLC
                move.l  #Nem_Titlecard,d6       ; art
                moveq   #Act_Count+1,d0         ; DPLC: ACTOVAL  
                cmpi.b	#3,$FFFFFE11
                beq	.act4
                cmpi.w  #$502,($FFFFFE10).w     ; FZ?
                bne     .Dplc			; load the DPLC
.act4		moveq   #Act_Count+2,d0         ; DPLC: OVAL
.Dplc		jmp     Load_DPLC2               ; load the DPLC
