                lea     DPLC_Obj34,a2           ; get the DPLC
                move.l  #Nem_Titlecard,d6       ; art
                move.w  #$B000,d4               ; the VRAM address
                moveq   #GHZ_DPLC-2,d0          ; DPLC: SGT
                jsr     Load_DPLC2               ; load the DPLC
                lea     DPLC_Obj34,a2           ; get the DPLC
                move.l  #Nem_Titlecard,d6       ; art
                moveq   #0,d0
                move.b  ($FFFFFE11).w,d0        ; load frame number
                cmpi.w  #$103,($FFFFFE10).w     ; LZ 4?
                bne.s   .notSBZ3                ; nope?
                moveq   #2,d0                   ; DPLC: ACT3
