		andi.b	#$70,d0		; is button A, B or C pressed?
		bne.s	Obj39_ChgMode	; if yes, branch
		tst.b	Anim_Frame(a0)
		bne.s	Obj39_Display
		tst.w	Anim_Dur(a0)		; has time delay reached zero?
		beq.s	Obj39_ChgMode	; if yes, branch
		subq.w	#1,Anim_Dur(a0)	; subtract 1 from time delay
		bra.w	DisplaySprite
