		moveq	#0,d1

		move.b	$FFFFFE11,d1		; get act id
		move.b	$FFFFFE10,d0		; get zone id
		add.w	d0,d0			; multiply by 2 (To get proper SRAM address)

		movea.l	SRAM_SlotID,a1		; get slot ID we are using
		lea	(a1,d0.w),a1		;
		adda.w	#$C,a1			; align to proper address

		bset	d1,(a1)			; set proper bit
		bne	.			; if was set, don't increment the score
		movea.l	SRAM_SlotID,a2		; get slot ID we are using
		addq.b	#3,SRAM_Progress(a2)	; increment the score

.		cmpi.b	#%00000111,(a1)		; check if all acts complete
		bne	.end 			; if not, branch
		move.b	$FFFFFE10,d0		; get zone id
		bset	d0,SRAM_ZoneComplete	; save complete zone
		
.end		rts
