Sonic_Setspeed:
	        moveq	#0,d0
		btst	#6,Status(a0)		; is underwater?
		beq	.NoUW			; if not, branch
		moveq	#8,d0

.NoUW		tst.b	($FFFFFE2E).w		; does Sonic have speed	shoes?
		beq	.NoShoes		; if not, branch
		addi.w	#16,d0

.NoShoes	tst.b	TurboMode_flag		; test for turbo mode
                beq	.NoTurbo		; if 0, branch
		addi.w	#32,d0

.NoTurbo	move.w	SpeedList(pc,d0.w),$FFFFF760	; set max speed
		move.w	SpeedList+2(pc,d0.w),$FFFFF762	; set acceleration
		move.w	SpeedList+4(pc,d0.w),$FFFFF764	; set deceleration

		tst.b	Current_Character	; test for Sonic
		beq	.end			; if is Sonic, branch

		moveq	#0,d0
		move.w	$FFFFF760,d0		; get speed
		lsr.w	#5,d0			; shift bits right by 5 (divide by 32)
		sub.w	d0,$FFFFF760		; subtract the result 

		move.w	$FFFFF762,d0		; get acceleration
		lsr.w	#3,d0			; shift bits right by 3 (divide by 8)
		sub.w	d0,$FFFFF762		; subtract the result

		move.w	$FFFFF764,d0		; get deceleration
		lsr.w	#3,d0			; shift bits right by 3 (divide by 8)
		sub.w	d0,$FFFFF764		; subtract the result

		cmpi.b	#1,Current_Character	; is Knuckles?
		bne	.end			; if not, branch
		subq.w	#1,$FFFFF762		; sub 1 from acceleration
		subq.w	#4,$FFFFF764		; sub 3 from deceleration
.end		rts

