ExtCam:                 
       		move.w	Extcam_pos.w,d1
		tst.b   Extcam_Use.w		; is extended camera on?
                beq.s	ExtCam_Center		; if not, see if camera position needs to be fixed

		tst.b	If_Spindash.w		; are we spindashing?
		bne.s	.movetoside		; if we are, branch
		tst.b	PeelOut_Flag.w		; are we peelouting?
		beq.s	.no			; if not, branch

.movetoside	btst	#0,Status(a0)		; check Sonic's direction
		beq.s	.forwards		; if facing right, move camera to right
		bne.s	.backwards		; if facing left, move camera to left
		       
.no		move.w	Inertia(a0),d0
		bpl.s	.notnegative
		neg.w	d0

.notnegative	cmp.w	#$480,d0
		bcs.s	ExtCam_Center2
		tst.w	Inertia(a0)
		bpl.s	.forwards

.backwards	addq.w	#2,d1
		cmp.w	#$A0+$38,d1
		bcs.s	ExtCam_save
		move.w	#$A0+$38,d1
		bra.s	ExtCam_save
; ---------------------------------------------------------------------------

.forwards	subq.w	#2,d1
		cmp.w	#$A0-$38,d1
		bcc.s	ExtCam_save
		move.w	#$A0-$38,d1
		bra.s	ExtCam_save
; ---------------------------------------------------------------------------

ExtCam_Center:
		move.w	Extcam_pos.w,d1

ExtCam_Center2:
		cmp.w	#$A0,d1
		beq.s	ExtCam_save
		bcc.s	.center2
		addq.w	#2,d1
		bra.s	ExtCam_save
; ---------------------------------------------------------------------------

.center2	subq.w	#2,d1

ExtCam_save:	
		move.w	d1,Extcam_pos.w
		rts
