Sonic_Roll:				; XREF: Obj01_MdNormal
		tst.b	($FFFFF7CA).w
		bne.s	Obj01_NoRoll
		tst.b	Sonic_ForceRollMode
		bne	Obj01_ChkRoll
		move.w	Inertia(a0),d0
		bpl.s	loc_13392
		neg.w	d0

loc_13392:
