		move.w	#-$500,d0
		cmp.w	Inertia(a0),d0
		bgt	locret_133E8
		cmpi.w	#$100,Inertia(a0)
		bge	locret_133E8
		move.w	d0,Inertia(a0)
.noflip		cmp.w	Inertia(a0),d0
