		move.w	#$4348,Art_Tile(a1)
		ori.b	#4,Render_Flags(a1)
		move.b	#$38,X_Visible(a1)

loc_150FE:
		dbf	d1,Obj66_Loop

		move.b	#$30,X_Visible(a0)
		move.b	#4,Priority(a0)
		move.w	#$3C,Off30(a0)
		move.b	#1,Off34(a0)
		move.b	Subtype(a0),Off38(a0)

