		bsr.w	Obj66_ChkSwitch
		tst.b	Render_Flags(a0)
		bpl.w	Obj66_Display
		move.w	#$30,d1
		move.w	d1,d2
		move.w	d2,d3
		addq.w	#1,d3
		move.w	X_pos(a0),d4
		jsr	SolidObject
		btst	#5,Status(a0)
		beq.w	Obj66_Display
		lea	Object_RAM,a1
		moveq	#$E,d1
		move.w	X_pos(a1),d0
		cmp.w	X_pos(a0),d0
		bcs.s	Obj66_GrabSonic
		moveq	#7,d1

