		move.b	#2,Anim(a1)	; make Sonic use "rolling" animation
		move.w	#$800,Inertia(a1)
		move.w	#0,X_Vel(a1)
		move.w	#0,Y_Vel(a1)
		bclr	#5,Status(a0)
		bclr	#5,Status(a1)
		bset	#1,Status(a1)
		clr.b	PeelOut_Flag
		clr.b	IF_Spindash
		move.w	X_pos(a1),d2
		move.w	Y_Pos(a1),d3
		bsr.w	Obj66_ChgPos
		add.w	d2,X_pos(a1)
