		beq.s	loc_151F8
		lea	Object_RAM,a1
		move.w	#0,X_Vel(a1)
		move.w	#$800,Y_Vel(a1)
		cmpi.b	#4,d0
		beq.s	loc_151F0
		move.w	#$800,X_Vel(a1)
		move.w	#$800,Y_Vel(a1)

loc_151F0:
		clr.b	($FFFFF7C8).w	; unlock controls
		subq.b	#4,Routine(a0)

loc_151F8:
		bsr.s	Obj66_ChkSwitch
		bsr.s	Obj66_ChgPos
		jmp	MarkObjGone

