		bpl.s	locret_15246
		move.b	#7,Anim_Dur(a0)
		move.b	Off34(a0),d1
		move.b	Anim_Frame(a0),d0
		add.b	d1,d0
		andi.b	#$F,d0
		move.b	d0,Anim_Frame(a0)

locret_15246:
		rts	
; End of function Obj66_ChkSwitch


