		move.w	X_pos(a0),X_pos(a1)
		move.w	Y_Pos(a0),Y_Pos(a1)

Hurt_Shield:
		move.b	#0,Shield_Type ; remove shield
		clr.b	Storeshield
		clr.b	Shield_RAM+Inertia
		clr.b	Shield_RAM+Routine2; clear secondary routine counter to load object properly
		move.b	#4,Routine(a0)
		jsr	Sonic_ResetOnFloor
		bset	#1,Status(a0)
		move.w	#-$400,Y_Vel(a0)	; make Sonic bounce away from the object
		move.w	#-$200,X_Vel(a0)
		btst	#6,Status(a0)
		beq.s	Hurt_Reverse
		move.w	#-$200,Y_Vel(a0)
		move.w	#-$100,X_Vel(a0)

Hurt_Reverse:
		move.w	X_pos(a0),d0
		cmp.w	X_pos(a2),d0
		bcs.s	Hurt_ChkSpikes	; if Sonic is left of the object, branch
