		sub.l	d1,d3
		bcc.s	loc_1D05E
		moveq	#0,d3

loc_1D05E:
		btst	#3,d4
		beq.s	loc_1D066
		add.l	d1,d3

loc_1D066:
		move.l	d2,Y_Pos(a0)
		move.l	d3,X_pos(a0)

Debug_BackItem:
		btst	#6,($FFFFF604).w ; is button A pressed?
		beq.s	Debug_MakeItem	; if not, branch
		btst	#5,($FFFFF605).w ; is button C pressed?
		beq.s	Debug_NextItem	; if not, branch
		subq.b	#1,($FFFFFE06).w ; go back 1 item
		bcc.s	Debug_NoLoop
		add.b	d6,($FFFFFE06).w
		bra.s	Debug_NoLoop
; ===========================================================================

Debug_NextItem:
		btst	#6,($FFFFF605).w ; is button A pressed?
		beq.s	Debug_MakeItem	; if not, branch
		addq.b	#1,($FFFFFE06).w ; go forwards 1 item
		cmp.b	($FFFFFE06).w,d6
		bhi.s	Debug_NoLoop
		move.b	#0,($FFFFFE06).w ; loop	back to	first item

Debug_NoLoop:
		bra.w	Debug_ShowItem
; ===========================================================================

Debug_MakeItem:
		btst	#5,($FFFFF605).w ; is button C pressed?
		beq.s	Debug_Exit	; if not, branch
		jsr	SingleObjLoad
		bne.s	Debug_Exit
		move.w	X_pos(a0),X_pos(a1)
		move.w	Y_Pos(a0),Y_Pos(a1)
		move.b	Mappings_Offset(a0),(a1)	; create object
		move.b	Render_Flags(a0),Render_Flags(a1)
		move.b	Render_Flags(a0),Status(a1)
		andi.b	#$7F,Status(a1)
		moveq	#0,d0
		move.b	($FFFFFE06).w,d0
		lsl.w	#3,d0
		move.b	4(a2,d0.w),Subtype(a1)
		move.b  #1,Off3F(a1)
		rts	
; ===========================================================================

Debug_Exit:
		btst	#4,($FFFFF605).w ; is button B pressed?
		beq.w	Debug_DoNothing	; if not, branch
		moveq	#0,d0
		move.w	d0,($FFFFFE08).w ; deactivate debug mode
		move.l	Player_MapLoc,Object_RAM+Mappings_Offset
		move.w	#$780,Object_RAM+Art_Tile
		move.b	d0,Object_RAM+Anim
		move.w	d0,X_Pos2(a0)
		move.w	d0,Y_Pos2(a0)
		move.w	($FFFFFEF0).w,($FFFFF72C).w ; restore level boundaries
		move.w	($FFFFFEF2).w,($FFFFF726).w
		cmpi.b	#$10,($FFFFF600).w ; are you in	the special stage?
		bne.s	Debug_Exitnormal	; if not, branch
		clr.w	($FFFFF780).w
		move.w	#$40,($FFFFF782).w ; set new level rotation speed
		move.l	Player_MapLoc,Object_RAM+Mappings_Offset
		move.w	#$780,Object_RAM+Art_Tile
		move.b	#2,Object_RAM+Anim
		bset	#2,Object_RAM+Status
		bset	#1,Object_RAM+Status
                rts
Debug_Exitnormal:
		clr.w   Object_RAM+X_Vel
		clr.w   Object_RAM+Y_Vel
		clr.w   Object_RAM+Inertia
		clr.w   Object_RAM+Off3C
		andi.b	#1,Object_RAM+Status	; clear everything but orientation
		bset	#1,Object_RAM+Status	; set to air
		move.b  #2,Object_RAM+Anim
		clr.b   Object_RAM+Routine2
		addq.l	#4,sp
		clr.b	Object_RAM+Off38
		ori.b	#1,($FFFFFE1F).w 	; update score	counter
		move.b	#1,($FFFFFE1D).w 	; update rings	counter
		move.b	#1,($FFFFFE1E).w 	; update time counter

		bsr	Hud_Base		; clear debug X/Y coordinates
		jmp	SetPlayerDisplay
Debug_DoNothing:
                rts	
; End of function Debug_Control


